# Sorting
# Сортировка
print(list(map(int, ','.join(str(','.join(str(x) for x in x)) for x in [[r for r in e ] for e in [[[[arr[arr1.index(q)] for w in range(len(arr1)) if q == arr1[w]] for q in range(len(arr1)) if q in arr1] for arr1 in [[ len([ 1 for j in range(len(arr)) if i!=j and arr[j] <= arr[i]]) for i in range(len(arr))]]] for arr in [list(map(int, input().split(' ')))]][0][0]]).split(','))))
